import { MachineConfig, send, Action, assign } from "xstate";


function say(text: string): Action<SDSContext, SDSEvent> {
    return send((_context: SDSContext) => ({ type: "SPEAK", value: text }))
}

function listen(): Action<SDSContext, SDSEvent> {
    return send('LISTEN')
}

const grammar: { [index: string]: { person?: string, day?: string, time?: string } } = {
    "John": { person: "John Appleseed" },
    "Julia": { person: "Julia Harvees" },
    "Victoria": {person: "Victoria Bloom"},
    "on Monday": { day: "Monday" },
    "on Tuesday": { day: "Tuesday" },
    "on Wednesday": { day: "Wednesday" },
    "on Thursday": { day: "Thursday" },
    "on Friday": { day: "Friday" },
    "on Saturday": { day: "Saturday" },
    "on Sunday": { day: "Sunday" },
    "at 8": { time: "8:00"},
    "at 10": { time: "10:00" },
    "at 9": { time: "9:00" },
    "at 11": {time: "11:00"},

}

const booleanGrammar = {
    "yes": true,
    "yep": true,
    "of course": true,
    "sure": true,
    "I want to": true,
    "no": false,
    "no way": false,
    "hell no": false,
}

const yes = booleanGrammar["yes"]
const no = booleanGrammar["no"]

const testGrammar: { [index: string]: { action?: string } } = {
    "appointment": { action: "Appointment"},
    "todo item": {action: "TODO item"},
    "to do": {action: "TODO item"},
    "timer": {action: "Timer"},
}
export const dmMachine: MachineConfig<SDSContext, any, SDSEvent> = ({
    initial: 'init',
    states: {
        init: {
            on: {
                CLICK: 'welcome'
            }
        },
        welcome: {
            initial: "prompt",
            on: {
                RECOGNISED: [{cond: (context) => (testGrammar[context.recResult] === "appointment"),
                    target: "appointment"
                },
        {cond: (context) => (testGrammar[context.recResult] === "to do item"),
        target: "todo_item"
        },
        {cond: (context) => (testGrammar[context.recResult] === "timer"),
        target: "timer"
        },
                { target: ".nomatch" }]
            },
            states: {
                prompt: { entry: say("What would you like to do? You can choose “Appointment”, “TODO item” and “Timer”."),
                on: { ENDSPEECH: "ask" }
            },
            ask: {
                entry: listen()
            },
            nomatch: {
                entry: say("Sorry I don't understand"),
                on: { ENDSPEECH: "prompt" }
            }
        }
    },
        todo_item: {
            initial: "prompt",
            on: { ENDSPEECH: "init" },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `Welcome to TODO Item`
                    }))
                },
		}
		},
        timer: {
            initial: "prompt",
            on: { ENDSPEECH: "init" },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `Welcome to timer`
                    }))
                },
		}
		},
        appointment: {
            initial: "prompt",
            on: { ENDSPEECH: "who" },
            states: {
                prompt: { entry: say("Let's create an appointment") }
            }
        },
        who: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "person" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { person: grammar[context.recResult].person } }),
                    target: "day"

                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: say("Who are you meeting with?"),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry I don't know them"),
                    on: { ENDSPEECH: "prompt" }
                }
            }
        },
        day: {
            initial: "prompt",
            on:{
                RECOGNISED: [{
                    cond: (context) => "day" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { day: grammar[context.recResult].day } }),
                    target: "wholeday"
                },
                { target: ".nomatch" }]
