            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `OK. ${context.person}. On which day is your meeting?`
                    })),
		    on: { ENDSPEECH: "ask" }
                },
		ask: {
		     entry: listen()
            },
	    nomatch: {
	    	entry: say("Sorry, I don't understand"),
		on: { ENDSPEECH: "prompt" }
	    }
	  }
        },
	wholeday: {
            initial: "prompt",
            on: {
                RECOGNISED: [{cond: (context) => (booleanGrammar[context.recResult] === no),
                    target: "time"
                },
		{cond: (context) => (booleanGrammar[context.recResult] === yes),
		target: "whole_day_confirmation"
		},
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `OK. ${context.person} on ${context.day}. Will it take the whole day?`
                    })),
		    on: { ENDSPEECH: "ask" }
                },
		ask: {
		     entry: listen()
            },
	    nomatch: {
	    	entry: say("Sorry, I don't understand"),
		on: { ENDSPEECH: "prompt" }
	            }
                }
	},
	time: {
            initial: "prompt",
            on: { RECOGNISED: [{
                    cond: (context) => "time" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { time: grammar[context.recResult].time } }),
                    target: "time_confirmation"
                },
                { target: ".nomatch" }]
		},
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `OK. ${context.person} on ${context.day}. What time is your meeting?`
                    })),
		    on: { ENDSPEECH: "ask" }
                },
		ask: {
		     entry: listen()
            },
	    nomatch: {
	    	entry: say("Sorry, I don't understand"),
		on: { ENDSPEECH: "prompt" }
	            }
                }
        },
	whole_day_confirmation: {
            initial: "prompt",
            on: {
                RECOGNISED: [{cond: (context) => (booleanGrammar[context.recResult] === no),
                    target: "init"
                },
		{cond: (context) => (booleanGrammar[context.recResult] === yes),
		target: "confirmed"
		},
                { target: ".nomatch" }]
            },

            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `Do you want to create an appointment with ${context.person} on ${context.day} for the whole day?`
                    })),
		    on: { ENDSPEECH: "ask" }
                },
		ask: {
		     entry: listen()
            },
	    nomatch: {
	    	entry: say("Sorry, I don't understand"),
		on: { ENDSPEECH: "prompt" }
	           }
                }

	},
 	time_confirmation: {
            initial: "prompt",
            on:  {
                RECOGNISED: [{cond: (context) => (booleanGrammar[context.recResult] === no),
                    target: "who"
                },
		{cond: (context) => (booleanGrammar[context.recResult] === yes),
		target: "confirmed"
		},
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                   entry: send((context) => ({
                        type: "SPEAK",
                        value: `Do you want to create an appointment with ${context.person} on ${context.day} at ${context.time}?`
                    })),
		    on: { ENDSPEECH: "ask" }
                },
		ask: {
		     entry: listen()
            },
	    nomatch: {
	    	entry: say("Sorry, I don't understand"),
		on: { ENDSPEECH: "prompt" }
	           }
                },
            },
	    confirmed: {
            initial: "prompt",
            on: { ENDSPEECH: "init" },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `Your appointment has been created!`
                    }))
                },
		}
		}
        }})
